import React, { useRef, useState } from 'react'

export default function Task(props) {
    const modal = useRef(null)
    const close = useRef(null)
    const [task, setTask] = useState({ taskDescription: props.taskDescription, dueDate: props.dueDate.split('T')[0] })
    function onChange(e) {
        setTask({[e.target.name]: e.target.value })
    }
    function openModal() {
        modal.current.click()
    }
    async function editTask(id) {
        setTask({ taskDescription: task.taskDescription, dueDate: task.dueDate })
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "PUT",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            },
            body: JSON.stringify({task})
        })
        close.current.click()
    }
    async function deleteTask(id) {
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "DELETE",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })

    }
    return (
        <>
            <button type="button" ref={modal} className="btn btn-primary" style={{ display: "none" }} data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="mb-3">
                                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                                <input type="text" className="form-control" id="taskDescription" value={task.taskDescription} onChange={onChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="dueDate" className="form-label">Due Date</label>
                                <input type="date" className="form-control" id="dueDate" value={new Date(task.dueDate)} onChange={onChange} />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" ref={close} data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary" onClick={() => { editTask(props.id) }}>Edit Task</button>
                        </div>
                    </div>
                </div>
            </div>
            <tr>
                <th scope="row">1</th>
                <td>{props.taskDescription}</td>
                <td>{props.dueDate}</td>
                <td><button className='btn btn-primary mx3' onClick={openModal}>Edit</button></td>
                <td><button className='btn btn-primary mx3' onClick={() => { deleteTask(props.taskId) }}>Delete</button></td>
            </tr>
        </>
    )
}
