import React, { useRef, useState } from 'react'

export default function Task(props) {
    const modal = useRef(null)
    const close = useRef(null)
    const date = new Date(props.dueDate)
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');

    const formattedDate = `${year}-${month}-${day}`;
    const [taskDescription, setTaskDescription] = useState(props.taskDescription)
    const [dueDate, setDueDate] = useState(formattedDate)
    function onChange(e) {
        if (e.target.name === 'dueDate') {
            const date = new Date(e.target.value)
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            const day = String(date.getDate()).padStart(2, '0');

            const formattedDate = `${year}-${month}-${day}`;
            setDueDate(formattedDate)
        } else {
            setTaskDescription(e.target.value)
        }
    }

    async function editTask(id) {
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "PUT",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            },
            body: JSON.stringify({ taskDescription: taskDescription, dueDate: dueDate })
        })
        close.current.click()
        // window.location.reload();
    }

    async function deleteTask(id) {
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "DELETE",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })
        // window.location.reload();
    }
    return (
        <>
            <button type="button" ref={modal} className="btn btn-primary" style={{ display: "none" }} data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="mb-3">
                                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                                <input type="text" className="form-control" name='taskDescription' id="taskDescription" value={taskDescription} onChange={onChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="dueDate" className="form-label">Due Date</label>
                                <input type="date" className="form-control" name='dueDate' id="dueDate" value={dueDate} min={new Date()} onChange={onChange} />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" ref={close} data-bs-dismiss="modal">Close</button>
                            <input type="submit" className="btn btn-primary" value="Edit Task" onClick={() => { editTask(props.taskId) }}/>
                        </div>
                    </div>
                </div>
            </div>
            <tr>
                <th scope="row">1</th>
                <td>{props.taskDescription}</td>
                <td>{new Date(props.dueDate).toLocaleDateString()}</td>
                <td><button className='btn btn-primary mx3' onClick={()=>modal.current.click()}>Edit</button></td>
                <td><button className='btn btn-primary mx3' onClick={() => { deleteTask(props.taskId) }}>Delete</button></td>
            </tr>
        </>
    )
}
