import React, { useContext, useRef, useState } from 'react'
import taskContext from '../context/TaskContext';

export default function Task(props) {
    const context = useContext(taskContext);
    const { deleteTask } = context;
    return (
        <>

            <tr>
                <th scope="row">1</th>
                <td>{props.taskDescription}</td>
                <td>{new Date(props.dueDate).toLocaleDateString()}</td>
                <td><button className='btn btn-primary mx3' onClick={() => props.updateTask({ taskDescription: props.taskDescription, dueDate: props.dueDate })}>Edit</button></td>
                <td><button className='btn btn-primary mx3' onClick={() => { deleteTask(props.taskId) }}>Delete</button></td>
            </tr>
        </>
    )
}
