import React, { useContext, useState } from 'react'
import taskContext from '../context/TaskContext';

export default function AddTask() {
    const context = useContext(taskContext);
    const { add, tasks, setTasks } = context;
    const [taskDescription, setTaskDescription] = useState("")
    const [dueDate, setDueDate] = useState("")

    function handleClick(e) {
        e.preventDefault()
        add(taskDescription, dueDate)
        setTaskDescription('');
        setDueDate('')
        setTasks
    }

    function onChange(e) {
        if (e.target.name === 'dueDate') {
            const date = new Date(e.target.value)
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            const day = String(date.getDate()).padStart(2, '0');

            const formattedDate = `${year}-${month}-${day}`;
            setDueDate(formattedDate)
        } else {
            setTaskDescription(e.target.value)
        }
    }
    return (
        <div className="container w-50">
            <div className="mb-3">
                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                <input type="text" className="form-control" name='taskDescription' id="taskDescription" onChange={onChange} />
            </div>
            <div className="mb-3">
                <label htmlFor="dueDate" className="form-label">Due Date</label>
                <input type="date" className="form-control" name='dueDate' id="dueDate" min={new Date().toLocaleDateString()} onChange={onChange} />
            </div>
            <input type="submit" className="btn btn-primary" value="Add Task" onClick={handleClick} />
        </div>
    )
}
