import { useState } from 'react';
import React from 'react'
import Task from './Task';

export default function Tasks() {
    const { tasks, setTasks } = useState([{ TASK_DESCRIPTION: "", DUE_DATE: "", TASK_ID: "" }]);
    async function getTasks() {
        const response = await fetch('http://localhost:5000/tasks', {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })
        const json=await response.json()
        setTasks(json)
    }
    getTasks()
    return (
        <div>
            <table class="table">
                <thead>
                    <tr>
                        <th scope="col">Task Description</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {tasks.map((task) => { return <Task taskId={task.TASK_ID} taskDescription={task.TASK_DESCRIPTION} dueDate={task.DUE_DATE} /> })}
                </tbody>
            </table>
        </div>
    )
}
