import { useContext, useEffect, useRef, useState } from 'react';
import React from 'react'
import Task from './Task';
import taskContext from '../context/TaskContext';

export default function Tasks() {
    const context = useContext(taskContext)
    const { tasks, setTasks, editTask } = context

    const modal = useRef(null)
    const close = useRef(null)

    const [taskId, setTaskId] = useState('')
    const [taskDescription, setTaskDescription] = useState('')
    const [dueDate, setDueDate] = useState('')

    function updateTask(currentTask) {
        modal.current.click()
        setTaskDescription(currentTask.taskDescription)
        setDueDate(currentTask.dueDate)
    }

    function handleClick(e) {
        editTask(taskId, taskDescription, dueDate)
        close.current.click()
    }

    function onChange(e) {
        if (e.target.name === 'dueDate') {
            const date = new Date(e.target.value)
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            const day = String(date.getDate()).padStart(2, '0');

            const formattedDate = `${year}-${month}-${day}`;
            setDueDate(formattedDate)
        } else {
            setTaskDescription(e.target.value)
        }
    }

    async function getTasks() {
        const response = await fetch('http://localhost:5000/tasks', {
            method: "GET",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })
        const json = await response.json()
        setTasks(json)
    }

    useEffect
        (() => { getTasks() }, []);
    return (
        <div>
            <button type="button" ref={modal} className="btn btn-primary" style={{ display: "none" }} data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="mb-3">
                                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                                <input type="text" className="form-control" name='taskDescription' id="taskDescription" value={taskDescription} onChange={onChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="dueDate" className="form-label">Due Date</label>
                                <input type="date" className="form-control" name='dueDate' id="dueDate" value={dueDate} onChange={onChange} />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" ref={close} data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary" onClick={handleClick}>Edit Task</button>
                        </div>
                    </div>
                </div>
            </div>
            <table className="table border">
                <thead>
                    <tr>
                        <th scope="col">Sr No</th>
                        <th scope="col">Task Description</th>
                        <th scope="col">Due Date</th>
                        <th scope="col" colSpan={2}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {tasks.map((task) => { return <Task key={task.TASK_ID} taskDescription={task.TASK_DESCRIPTION} dueDate={task.DUE_DATE} taskId={task.TASK_ID} updateTask={updateTask} /> })}
                </tbody>
            </table>
        </div>
    )
}
