import React, { useState } from 'react'

export default function AddTask() {
    const [description, setDescription] = useState("")
    const [dueDate, setDueDate] = useState("")
   
    function onChange(e) {
        if (e.target.name === 'dueDate') {
            const date = new Date(e.target.value)
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            const day = String(date.getDate()).padStart(2, '0');

            const formattedDate = `${year}-${month}-${day}`;
            setDueDate({ [e.target.name]: formattedDate })
        } else {
            setDescription({ [e.target.name]: e.target.value })
        }
    }

    async function add() {
        await fetch('http://localhost:5000/tasks', {
            method: "POST",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            },
            body: JSON.stringify({ taskDescription: description, dueDate: dueDate })
        })
    }
    return (
        <div>
            <div className="mb-3">
                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                <input type="text" className="form-control" name='taskDescription' id="taskDescription"  onChange={onChange} />
            </div>
            <div className="mb-3">
                <label htmlFor="dueDate" className="form-label">Due Date</label>
                <input type="date" className="form-control" name='dueDate' id="dueDate" onChange={onChange} />
            </div>
            <button type="button" className="btn btn-primary" onClick={() => { add() }}>Add Task</button>
        </div>
    )
}
