import React, { useContext, useRef } from 'react'
import taskContext from '../context/TaskContext';

export default function UpdateModal(props) {
    const { modalTaskId, modalTaskDescription, setModalTaskDescription, modalTaskDueDate, setModalTaskDueDate } = props
    const context = useContext(taskContext);
    const { tasks, setTasks, editTask } = context
    const close = useRef();
    function handleClick() {
        editTask(modalTaskId, modalTaskDescription, modalTaskDueDate)
        let newTasks = tasks;
        for (let index = 0; index < newTasks.length; index++) {
            const element = newTasks[index];
            console.log(element)
            if (element.TASK_ID === modalTaskId) {
                newTasks[index].TASK_ = modalTaskDescription;
                newTasks[index].dueDate = modalTaskDueDate;
                break;
            }
        }
        setTasks(newTasks);
        close.current.click()
    }
    function onChange(e) {
        if (e.target.name === 'dueDate') {
            setModalTaskDueDate(new Date(e.target.value).toLocaleDateString())
        } else {
            setModalTaskDescription(e.target.value)
        }
    }
    return (
        <div>
            <button type="button" ref={props.modal} className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                        </div>
                        <div className="modal-body">
                            <div className="mb-3">
                                <label htmlFor="taskDescription" className="form-label">Task Description</label>
                                <input type="text" className="form-control" name='taskDescription' id="taskDescription" value={modalTaskDescription} onChange={onChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="dueDate" className="form-label">Due Date</label>
                                <input type="date" className="form-control" name='dueDate' id="dueDate" min={new Date().toLocaleDateString()} value={modalTaskDueDate} onChange={onChange} />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button ref={close} type="button" className="btn btn-secondary" data-bs-dismiss="modal" >Close</button>
                            <input type="submit" className="btn btn-primary" value="Edit Task" onClick={handleClick} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
