import { useState } from 'react';
import TaskContext from './TaskContext'

const TaskState = (props) => {
    const [tasks, setTasks] = useState([{ taskDescription: '', dueDate: '' }]);
    async function getTasks() {
        const response = await fetch('http://localhost:5000/tasks', {
            method: "GET",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })
        const json = await response.json()
        setTasks(json)
    }

    async function add(taskDescription, dueDate) {
        await fetch('http://localhost:5000/tasks', {
            method: "POST",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            },
            body: JSON.stringify({ taskDescription: taskDescription, dueDate: dueDate })
        })
        setTasks(tasks.concat([{ taskDescription: taskDescription, dueDate: dueDate }]))
    }

    async function editTask(id, taskDescription, dueDate) {
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "PUT",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            },
            body: JSON.stringify({ taskDescription: taskDescription, dueDate: dueDate })
        })
        let newTasks = JSON.parse(JSON.stringify(tasks))
        for (let index = 0; index < newTasks.length; index++) {
            const element = newTasks[index];
            if (element._id === id) {
                newTasks[index].taskDescription = taskDescription;
                newTasks[index].dueDate = dueDate;
                break;
            }
        }
        setTasks(newTasks);
    }

    async function deleteTask(id) {
        await fetch(`http://localhost:5000/tasks/${id}`, {
            method: "DELETE",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
                "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjo1fSwiaWF0IjoxNzA0NzY1ODcwfQ.gee66hhxU7DmQMOReq9B67VqMzdP9j22rmvjoRwsKzE"
            }
        })
